import { GoogleMap, useJsApiLoader, MarkerF } from "@react-google-maps/api";
import React, { useState, useMemo, useCallback, useRef, useEffect } from "react";

// import { getRestaurantsData } from '../../api/getRestaurants.js'
import styles from "./map.module.css";

export default function Map() {
  const [places, setPlaces] = useState();
  const [coordinates, setCoordinates] = useState();

  const mapStyles = styles["map-container"];
  const options = useMemo(() => ({
    disableDefaultUI: true,
    clickableIcons: false,
  }), []);

  function positionPromise(positionCall) {
    return new Promise((resolve, reject) => {
        positionCall((successResponse) => {
            resolve(successResponse);
        }, (errorResponse) => {
            reject(errorResponse);
        });
    });
}

  const onLoad = useCallback( async function onLoad (mapInstance){
    const geocoder = new window.google.maps.Geocoder
    const service = new window.google.maps.places.PlacesService(mapInstance)

    let currentLocation;
    let coordinate; 

    const getPositionCallback = ({coords: {latitude, longitude}}) => {
      coordinate = { lat: latitude, lng: longitude };
      setCoordinates(coordinate);
    }

    const result = await positionPromise(navigator.geolocation.getCurrentPosition);

    navigator.geolocation.getCurrentPosition(getPositionCallback);
    console.log(result)

    geocoder.geocode({'location': coordinate}, function(results, status){
      if (status === window.google.maps.GeocoderStatus.OK) {
        if (results[1]) {
          currentLocation = results[1].place_id
        } else {
          console.log('No results found')
        }
      } else {
        console.log('Geocoder failed due to: ' + status)
      }
    })

    service.getDetails({
      placeId: currentLocation
    }, function (place, status) {
      console.log('Place details:', place);
    });
  });

  const {isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  })


  // useEffect(() => {
  //   // getRestaurantsData()
  //   //   .then((data) => {
  //   //     setPlaces(data);
  //   //   })
  //   const map = window.google.maps.Map(document.getElementById('map'))
  //   const service = window.google.maps.places.PlacesService(map)
  //   service.getDetails({
  //     placeId: 'ChIJN1t_tDeuEmsRUsoyG83frY4'
  //   }, function (place, status) {
  //     console.log('Place details:', place);
  //   });
  // })

  const RenderMap = () => {
    return (
      <div>
        <div className={styles["search"]}>
        </div>
        <div className={styles["map-container"]}>
          <GoogleMap
            id={'map'}
            zoom={10}
            center={coordinates}
            mapContainerClassName={mapStyles}
            options={options}
            onLoad={onLoad}
          >
            <MarkerF position={coordinates}></MarkerF>
          </GoogleMap>
        </div>
      </div>
    );
  }
  if (loadError) {
    return <div>Map cannot be loaded.</div>
  }
  return isLoaded ? RenderMap() : <div></div>
}
