import { GoogleMap, useJsApiLoader, MarkerF } from "@react-google-maps/api";
import { useState, useMemo, useCallback, useRef, useEffect } from "react";

// import { getRestaurantsData } from '../../api/getRestaurants.js'
import styles from "./map.module.css";

export default function Map() {
  const [places, setPlaces] = useState();
  const [bounds, setBounds] = useState(null);
  const [coordinates, setCoordinates] = useState();

  const mapStyles = styles["map-container"];
  const mapRef = useRef();
  const options = useMemo(() => ({
    disableDefaultUI: true,
    clickableIcons: false,
  }), []);
  const {isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY
  })

  const RenderMap = () => {
    const onLoad = useCallback(map => (mapRef.current = map), []);

    return (
      <div>
        <div className={styles["search"]}>
        </div>
        <div className={styles["map-container"]}>
          <GoogleMap
            id={'map'}
            zoom={10}
            center={coordinates}
            mapContainerClassName={mapStyles}
            options={options}
            onLoad={onLoad}
          >
            <MarkerF position={coordinates}></MarkerF>
          </GoogleMap>
        </div>
      </div>
    );
  }
  if (loadError) {
    return <div>Map cannot be loaded.</div>
  }
  return isLoaded ? RenderMap() : <div></div>
}
